        theop=ContractDOrdinaryWrapper(v,l,evenEdges)
        fsize = -1
        try
          #D = load_matrix(theop)
          #if D==[]
          #  nrEntries = 0
          #else
          #  nrEntries = nnz(D)
          #end
          fff = get_file_name(theop)
          if isfile(fff)
            fsize=filesize(fff)
          end 
        catch
